-- ----------------------------
DROP TABLE IF EXISTS `article`;
CREATE TABLE `article` (
  `id` mediumint(8) unsigned NOT NULL AUTO_INCREMENT,
  `cat_id` smallint(5) NOT NULL DEFAULT '0' COMMENT '分类id',
  `title` varchar(150) NOT NULL DEFAULT '' COMMENT '文章题目',
  `source` varchar(100) CHARACTER SET utf8mb4 NOT NULL DEFAULT '' COMMENT '来源',
  `browser` int(11) NOT NULL DEFAULT '0' COMMENT '浏览量',
  `keywords` varchar(255) CHARACTER SET utf8mb4 NOT NULL COMMENT '关键字',
  `content` longtext NOT NULL COMMENT '文章内容',
  `cover` varchar(255) CHARACTER SET utf8mb4 NOT NULL DEFAULT '' COMMENT '封面图',
  `description` varchar(255) NOT NULL DEFAULT '' COMMENT '描述',
  `sort` smallint(10) unsigned NOT NULL DEFAULT '999',
  `is_top` tinyint(1) NOT NULL DEFAULT '1' COMMENT '是否置顶 1：是 2：否',
  `is_recommend` tinyint(1) NOT NULL DEFAULT '1' COMMENT '1:是 2：否',
  `operator_name` varchar(150) NOT NULL DEFAULT '' COMMENT '操作人昵称',
  `operator_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '操作人id',
  `status` tinyint(1) unsigned NOT NULL DEFAULT '1' COMMENT '1:正常 2：禁用',
  `is_delete` tinyint(1) unsigned NOT NULL DEFAULT '1' COMMENT '1:正常2：删除',
  `created_at` datetime DEFAULT NULL,
  `updated_at` datetime DEFAULT NULL,
  PRIMARY KEY (`id`) USING BTREE,
  KEY `cat_id` (`cat_id`) USING BTREE,
  KEY `title` (`title`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=4 DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC COMMENT='文章表';

